export interface SwppCliConfig {
    /** 网站根目录，可以是绝对路径也可以是相对路径，swpp 根据系统规则进行判断 */
    webRoot: string;
    /** 配置文件所在的相对路径（越靠前优先级越高） */
    configFiles: string[] | string;
    /** dom js 的相对路径（相对于网站根目录，`.js` 结尾） */
    domJsPath?: string;
    /**
     * 需要被排除的 html 文件名，正则表达式，区分大小写
     *
     * 该正则表达式将会与文件路径（相对于网站根目录）进行匹配
     */
    excludes?: string[];
    /** 是否生成 sw 文件 */
    serviceWorker?: boolean;
    /** 是否向 HTML 中插入注册代码 */
    auto_register?: boolean;
    /** 是否自动生成 DOM JS 并在 HTML 插入 <script> */
    gen_dom?: boolean;
    /** diff json 的相对路径（相对于项目根目录）或绝对路径（以 .json 结尾） */
    diffJsonPath?: string;
}
export declare function initCommand(): Promise<void>;
